import React, { useRef, useEffect } from "react"; import { motion } from 'framer-motion';

const projects = [
  {
    id: 1,
    image:
      "data:image/jpeg;base64,",
    title: "Aplicación de comercio electrónico",
  },
  {
    id: 2,
    image:
      "https://www.timecamp.com/es/blog/wp-content/uploads/2021/05/MeisterTask-839x525.png",
    title: "Sistema de gestión de tareas",
  },
  {
    id: 3,
    image:
      "data:image/jpeg;base64,",
    title: "Portafolio interactivo",
  },
  {
    id: 4,
    image:
      "data:image/jpeg;base64,",
    title: "Red social para desarrolladores",
  },
  {
    id: 5,
    image:
      "https://www.elearningfacil.com/wp-content/uploads/elfacil-banner-blog_05-21-post-7-6.jpg",
    title: "Plataforma de aprendizaje en línea",
  },
];

const Proyectos: React.FC = () => {
  const carouselRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const interval = setInterval(() => {
      if (carouselRef.current) {
        carouselRef.current.scrollLeft += 1;
        if (
          carouselRef.current.scrollLeft >=
          carouselRef.current.scrollWidth - carouselRef.current.offsetWidth
        ) {
          carouselRef.current.scrollLeft = 0;
        }
      }
    }, 30);

    return () => clearInterval(interval);
  }, []);

  return (
    <section id="proyectos" className="py-20 bg-gray-200 dark:bg-gray-800 overflow-hidden">
      <div className="container mx-auto">
        <motion.h2
          className="text-3xl font-bold text-center mb-10 text-blue-800 dark:text-blue-300"
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          Proyectos de Estudiantes
        </motion.h2>
        <motion.div
          className="relative overflow-hidden"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5, delay: 0.2 }}
        >
          <div ref={carouselRef} className="flex space-x-4 overflow-x-hidden">
            {projects.map((project, index) => (
              <motion.div
                key={project.id}
                className="flex-none w-64 md:w-80"
                initial={{ opacity: 0, x: 100 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
              >
                <motion.div
                  className="bg-white dark:bg-gray-700 rounded-lg shadow-md overflow-hidden"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <img
                    src={project.image}
                    alt={project.title}
                    className="w-full h-48 object-cover"
                  />
                  <div className="p-4">
                    <h3 className="text-lg font-semibold text-blue-800 dark:text-blue-300">
                      {project.title}
                    </h3>
                  </div>
                </motion.div>
              </motion.div>
            ))}
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Proyectos;
